import React, { useState } from 'react';
import Question from './Question';

export default function SingleGame() {
    const timerRef = 0;
    const [questionIndex, setQuestionIndex] = useState(0);
    const [time, setTime] = useState(10);


    // TODO: get these questions from somewhere else!
    const questions = [
        {
            question: 'Who is the author of "The Bitcoin Standard"?',
            answers: [
                'Saifedean Ammous',
                'Parker Lewis',
                'Michael Saylor',
                'Preston Pysh'
            ],
            labels: [
                'People'
            ]
        },
        {
            question: 'Who is the creator of Bitcoin"?',
            answers: [
                'Nick Szabo',
                'Craig Wright',
                'Satoshi Nakomoto',
                'Adam Back'
            ],
            labels: [
                'People'
            ]
        }
    ]

    const countDown = () => {
        let seconds = time - 1;
        setTimer(seconds);

        if (seconds == 0) { 
            clearInterval(this.timerRef);
        }
    }

    const startTimer = () => {
        setTime(10);
        timerRef = setInterval(countDown, 1000);
    }


    return (
        <Question
            time={time}
            question={questions[questionIndex]}
        />
    );
}
