import React, { useState, useEffect, useCallback } from 'react';
import Question from './Question';

export default function SingleGame() {
    let timer = null;
    const [questionIndex, setQuestionIndex] = useState(0);
    const [time, setTime] = useState(10);

    // fires when the game starts
    useEffect(() => {
        clearInterval(timer);

        setTimeout(() => {
            timer = startTimer(10);
        }, 2500);

        return () => {
            clearInterval(timer);
        };
    }, [questionIndex]);

    // TODO: get these questions from somewhere else!
    const questions = [
        {
            question: 'Who is the author of "The Bitcoin Standard"?',
            answers: [
                'Saifedean Ammous',
                'Parker Lewis',
                'Michael Saylor',
                'Preston Pysh'
            ],
            labels: [
                'People'
            ],
            difficulty: 0
        },
        {
            question: 'Who is the creator of Bitcoin?',
            answers: [
                'Nick Szabo',
                'Craig Wright',
                'Satoshi Nakomoto',
                'Adam Back'
            ],
            labels: [
                'People'
            ],
            difficulty: 0
        }
    ]

    const startTimer = (newTime) => {
        setTime(newTime);
        return setInterval(countDown, 1000);
    }

    const countDown = () => {
        setTime(prevTime => {
            const newTime = prevTime - 1;
            if (newTime === 0) {
                clearInterval(timer);
            }
            return newTime;
        });
    }

    const onClickAnswer = (index) => {
        console.log('clicked', index);
        setTime(10); // TODO: maybe this shouldnt be here
        setQuestionIndex(prevQuestionIndex => setQuestionIndex(prevQuestionIndex + 1));
    }


    return (
        <Question
            time={time}
            question={questions[questionIndex]}
            onClickAnswer={onClickAnswer}
        />
    );
}
