import React from 'react';
import { View, StyleSheet, Text, Pressable } from 'react-native';
import { Icon } from 'react-native-elements'

export default function InCorrect(props) {
    return (
        <View style={styles.incorrectContainer}>
            <Icon
                name='x'
                type='feather'
                color='#ff0000'
                size={128}
            />
            <Text style={styles.incorrectText}>That's Incorrect</Text>
            <Text style={styles.incorrectMessage}>"If you don't believe me or don't get it, I don't have time to try to convince you, sorry" - Satoshi Nakamoto</Text>
            <Pressable style={styles.button} onPress={props.nextQuestion}>
                <Text style={styles.buttonText}>Next question</Text>
            </Pressable>
        </View>
    );
}

const styles = StyleSheet.create({
    incorrectContainer:{
        boxSizing: 'border-box'
    },
    incorrectText: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: '3rem',
    },
    incorrectMessage: {
        fontWeight: 'bold',
        fontSize: '2rem'
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'blue',
        color: 'white',
        cursor: 'pointer',
        marginBottom: 10,
        marginTop: 50
    },
    buttonText: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',
    }
});
