import React, { useRef, useEffect } from 'react';
import { Animated, View, StyleSheet, Text, Pressable } from 'react-native';
import { Icon } from 'react-native-elements'

export default function InCorrect(props) {
    const scaleAnimIcon = useRef(new Animated.Value(1)).current;

    useEffect(() => {
        Animated.timing(
            scaleAnimIcon,
            {
              toValue: 1,
              duration: 1000,
            }
          ).start();
    }, [scaleAnimIcon]);

    return (
        <View style={styles.incorrectContainer}>
            <Animated.View
                style={{
                    /*transform: [
                        {
                            scaleX: scaleAnimIcon.interpolate({
                                inputRange: [0, 1],
                                outputRange: [1, 5]
                            })
                        },
                        {
                            scaleY: scaleAnimIcon.interpolate({
                                inputRange: [0, 1],
                                outputRange: [1, 7.5]
                            })
                        }
                    ]*/
                }}
            >
                <Icon
                    name='x'
                    type='feather'
                    color='#ff0000'
                    size={128}
                />
                <Text style={styles.incorrectText}>That's Incorrect</Text>
            </Animated.View>
            <Text style={styles.incorrectMessage}>"If you don't believe me or don't get it, I don't have time to try to convince you, sorry" - Satoshi Nakamoto</Text>
            <Pressable style={styles.button} onPress={props.nextQuestion}>
                <Text style={styles.buttonText}>Next question</Text>
            </Pressable>
        </View>
    );
}

const styles = StyleSheet.create({
    incorrectContainer:{
        boxSizing: 'border-box'
    },
    incorrectText: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: '3rem',
    },
    incorrectMessage: {
        fontWeight: 'bold',
        fontSize: '2rem',
        marginTop: 50
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'blue',
        color: 'white',
        cursor: 'pointer',
        marginBottom: 10,
        marginTop: 50
    },
    buttonText: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',
    }
});
