import React, { useState } from 'react';
import Welcome from './Welcome';
import SingleGame from './SingleGame';

export default function App() {
  const [screen, setScreen] = useState(0);


  const getContent = () => {
    if (screen === 0) {
      return <Welcome playSingleGame={() => setScreen(1)} />;
    } else if (screen === 1) {
      return <SingleGame />
    }

    return '';
  }

  return getContent();
}
