import React, { useEffect, useRef, useState } from 'react';
import { Animated, View, StyleSheet, Text } from 'react-native';

export default function Transition(props) {
    let timer = null;
    const fadeAnimReady = useRef(new Animated.Value(0)).current;
    const fadeAnimStarting = useRef(new Animated.Value(0)).current;
    const [time, setTime] = useState(3);

    useEffect(() => {
        Animated.timing(
            fadeAnimReady,
            {
              toValue: 1,
              duration: 1000,
            }
          ).start();
    }, [fadeAnimReady]);

    useEffect(() => {
        Animated.timing(
            fadeAnimStarting,
            {
              toValue: 1,
              duration: 500,
              delay: 1100
            }
          ).start(({ finished }) => {
            if (finished) {
                timer = setInterval(() => {
                    setTime(prevTime => {
                        const newTime = prevTime - 1;
                        if (newTime === 0) {
                            console.log('TIME IS ZERO');
                            clearInterval(timer);
                        }
                        return newTime;
                    });
                }, 1000);
            }
          });
    }, [fadeAnimStarting]);

    return (
        <View style={styles.transitionContainer}>
            <Animated.View
                style={{
                    opacity: fadeAnimReady
                }}
            >
                <Text style={styles.readyText}>Ready?</Text>
            </Animated.View>
            <Animated.View
                style={{
                    opacity: fadeAnimStarting
                }}
            >
                <Text style={styles.startingInText}>Starting in...{time}</Text>
            </Animated.View>
        </View>
    );
}

const styles = StyleSheet.create({
    transitionContainer:{
      boxSizing: 'border-box'
    },
    readyText: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: '4rem',
        paddingTop: 20,
        paddingBottom: 20
    },
    startingInText: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: '2rem'
    }
});
