import React from 'react';
import { StyleSheet, Text, View, Pressable, ProgressBar } from 'react-native';

export default function SingleGame(props) {
    return (
        <View style={styles.questionContainer}>
            <View>
                <Text style={styles.timerText}>{props.time}</Text>
                <ProgressBar
                    color="rgb(121, 75, 196)"
                    progress={() => props.time / 10}
                    trackColor="rgba(121, 75, 196, 0.3)"
                />
            </View>
            <Text style={styles.question}>
                {props.question.question}
            </Text>
            <Pressable style={styles.button} onPress={() => props.onClickAnswer(0)}>
                <Text style={styles.buttonText}>{props.question.answers[0]}</Text>
            </Pressable>
            <Pressable style={styles.button} onPress={() => props.onClickAnswer(1)}>
                <Text style={styles.buttonText}>{props.question.answers[1]}</Text>
            </Pressable>
            <Pressable style={styles.button} onPress={() => props.onClickAnswer(2)}>
                <Text style={styles.buttonText}>{props.question.answers[2]}</Text>
            </Pressable>
            <Pressable style={styles.button} onPress={() => props.onClickAnswer(3)}>
                <Text style={styles.buttonText}>{props.question.answers[3]}</Text>
            </Pressable>
        </View>
    );
}

const styles = StyleSheet.create({
    questionContainer:{
      boxSizing: 'border-box'
    },
    timerText: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: '2rem'
    },
    question: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: '2rem'
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'blue',
        color: 'white',
        cursor: 'pointer',
        marginBottom: 10,
        marginTop: 10
    },
    buttonText: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',
    }
  });
