import React, { useState, useEffect, useCallback } from 'react';
import Incorrect from './Incorrect';
import Ready from './Ready';
import Question from './Question';

export default function SingleGame() {
    let timer = null;
    const [questionIndex, setQuestionIndex] = useState(0);
    const [time, setTime] = useState(1000);
    const [showReady, setShowReady] = useState(true);
    const [showIncorrect, setShowIncorrect] = useState(false);

    // fires when the game starts
    useEffect(() => {
        // TODO fix the timer
        /*clearInterval(timer);

        setTimeout(() => {
            timer = startTimer(1000);
        }, 2500);

        return () => {
            clearInterval(timer);
        };*/
    }, [questionIndex]);

    // TODO: get these questions from somewhere else!
    const questions = [
        {
            id: 0,
            question: 'Who is the author of "The Bitcoin Standard"?',
            answers: [
                'Saifedean Ammous',
                'Parker Lewis',
                'Michael Saylor',
                'Preston Pysh'
            ],
            labels: [
                'People'
            ],
            difficulty: 0
        },
        {
            id: 1,
            question: 'Who invented Bitcoin?',
            answers: [
                'Craig Wright',
                'Vitalik Butrin',
                'Satoshi Nakomoto',
                'Elon Musk'
            ],
            labels: [
                'People'
            ],
            difficulty: 0
        },
        {
            id: 2,
            question: 'Bitcoin uses ___ algorithm',
            answers: [
                'Proof of Work',
                'Proof of Stake',
                'Proof of Keys',
                'Proof of Bananas'
            ],
            labels: [
                'Bitcoin'
            ],
            difficulty: 0
        }
    ]

    const startTimer = (newTime) => {
        setTime(newTime);
        return setInterval(countDown, 10);
    }

    const countDown = () => {
        setTime(prevTime => {
            const newTime = prevTime - 1;
            if (newTime === 0) {
                clearInterval(timer);
            }
            return newTime;
        });
    }

    const onClickAnswer = (index) => {
        console.log('clicked', index); // TODO: index is used to check if the question was correct/incorrect
        setShowIncorrect(true);
        
        // setTime(1000); // TODO: maybe this shouldnt be here
        // setQuestionIndex(prevQuestionIndex => setQuestionIndex(prevQuestionIndex + 1));
    }

    const nextQuestion = () => {
        setShowCorrect(false);
        setShowIncorrect(false);
        setQuestionIndex(prevQuestionIndex => prevQuestionIndex + 1);
    }

    const getContent = () => {
        if (showReady) {
            return <Ready setShowReady={setShowReady} />
        } else if (showIncorrect) {
            return <Incorrect nextQuestion={nextQuestion} correct />;
        }

        return (
            <Question
                time={time}
                question={questions[questionIndex]}
                onClickAnswer={onClickAnswer}
            />
        );
    }

    return getContent();
}
