import React, { useState, useEffect, useCallback } from 'react';
import Ready from './Ready';
import Question from './Question';

export default function SingleGame() {
    let timer = null;
    const [questionIndex, setQuestionIndex] = useState(0);
    const [time, setTime] = useState(1000);
    const [showReady, setShowReady] = useState(true);

    // fires when the game starts
    useEffect(() => {
        clearInterval(timer);

        setTimeout(() => {
            timer = startTimer(1000);
        }, 2500);

        return () => {
            clearInterval(timer);
        };
    }, [questionIndex]);

    // TODO: get these questions from somewhere else!
    const questions = [
        {
            id: 0,
            question: 'Who is the author of "The Bitcoin Standard"?',
            answers: [
                'Saifedean Ammous',
                'Parker Lewis',
                'Michael Saylor',
                'Preston Pysh'
            ],
            labels: [
                'People'
            ],
            difficulty: 0
        },
        {
            id: 1,
            question: 'Who is the creator of Bitcoin?',
            answers: [
                'Nick Szabo',
                'Craig Wright',
                'Satoshi Nakomoto',
                'Adam Back'
            ],
            labels: [
                'People'
            ],
            difficulty: 0
        },
        {
            id: 2,
            question: 'What is the algorithm called in which bitcoin uses to "mine" new coins?',
            answers: [
                'Proof of stake',
                'Proof of work',
                'Proof of keys',
                'RSA'
            ],
            labels: [
                'Functionality'
            ],
            difficulty: 0
        }
    ]

    const startTimer = (newTime) => {
        setTime(newTime);
        return setInterval(countDown, 10);
    }

    const countDown = () => {
        setTime(prevTime => {
            const newTime = prevTime - 1;
            if (newTime === 0) {
                clearInterval(timer);
            }
            return newTime;
        });
    }

    const onClickAnswer = (index) => {
        console.log('clicked', index); // TODO: index is used to check if the question was correct/incorrect
        setTime(1000); // TODO: maybe this shouldnt be here
        setQuestionIndex(prevQuestionIndex => setQuestionIndex(prevQuestionIndex + 1));
    }

    const getContent = () => {
        if (showReady) {
            return <Ready />
        }

        return (
            <Question
                time={time}
                question={questions[questionIndex]}
                onClickAnswer={onClickAnswer}
            />
        );
    }

    return getContent();

    /*return (
        <Question
            time={time}
            question={questions[questionIndex]}
            onClickAnswer={onClickAnswer}
        />
    );*/
}
