import React from 'react';
import { StyleSheet, Text, View, Pressable } from 'react-native';

export default function SingleGame() {
    return (
        <View style={styles.questionContainer}>
            <Text style={styles.question}>
                Who is the author of "The Bitcoin Standard"?
            </Text>
            <Pressable style={styles.button} onPress={() => console.log('clicked 1')}>
                <Text style={styles.buttonText}>Saifedean Ammous</Text>
            </Pressable>
            <Pressable style={styles.button} onPress={() => console.log('clicked 2')}>
                <Text style={styles.buttonText}>Parker Lewis</Text>
            </Pressable>
        </View>
    );
}

const styles = StyleSheet.create({
    questionContainer:{
      border: '1px solid red',
      boxSizing: 'border-box'
    },
    question: {
        textAlign: "center",
        fontWeight: "bold",
        fontSize: "2rem"
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'blue',
        color: 'white'
    },
    buttonText: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',
    }
  });
